import { Component, Input } from '@angular/core';
import { Observable } from 'rxjs';
import { TasksService } from './tasks.service';
import { Task } from './schema/tasks';


@Component({
  selector: 'app-tasks',
  templateUrl: './tasks.component.html',
  styleUrls: ['./tasks.component.css'],
  providers:[TasksService]
})
export class TasksComponent {
  tasks: Task[]=[];
  task: Task = new Task;
  _id:string='';
  task_name:string='';
  complete:boolean=false;
constructor(private taskService:TasksService){

}

ngOnInit(){
this.viewTasks();

}

viewTasks(){
  this.taskService.getTasks().subscribe({
    next: (data:any) => {
      this.tasks = data;
      console.log(data);
    },
    error: (e:any) => console.error(e)
  });
}

viewTask(id:any){
 //var tasks=this.tasks;
this.taskService.viewTask(id).subscribe({next:(data:any)=>{
  this.task=data;
  this._id=this.task._id;
  this.task_name=this.task.task_name;
  this.complete=this.task.complete;
}})
}


addeditTask(addoredit:any){

  const newTask={
    task_name:this.task_name,
    complete:this.complete
  }
  if(this._id=='0'){
    //this.taskService.addTask(newTask).subscribe(({task})=>{this.tasks.push(task);})
    this.taskService.addTask(newTask).subscribe(data=>alert('Task added'));
  }
  else {
    this.taskService.updateTask(this._id,newTask).subscribe(data=>alert('Task updated'));
  }

}


deleteTask(id:any){
  var tasks=this.tasks;
this.taskService.deleteTask(id).subscribe(data=>{
 // if(data.n==1){
    for (var i=0;i<tasks.length;i++){
      if(tasks[i]._id==id){
        tasks.splice(i,1);
      }
   // }
  }
})
}

reloadpage(){
  window.location.reload();
}

}
<div class="jumbotron text-center bg-primary text-white">
  <h1>Task Manager</h1>
  <p>Organize your tasks efficiently and stay productive! 🚀</p>
</div>

<div class="container mt-4">
  <button type="button" class="btn btn-primary mb-3" data-toggle="modal" data-target="#myModal" (click)="_id='0';myForm.reset();">
    + Add Task
  </button>

  <!-- Task Table -->
  <div class="p-3">
    <table class="table table-bordered table-hover">
      <thead class="thead-dark">
        <tr>
          <th>Task</th>
          <th>Completed?</th>
          <th>Edit</th>
          <th>Delete</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let task of tasks" [ngClass]="{'table-success': task.complete, 'table-light': !task.complete}">
          <td>{{ task.task_name }}</td>
          <td>
            <span class="badge" [ngClass]="task.complete ? 'badge-success' : 'badge-danger'">
              {{ task.complete ? 'Yes' : 'No' }}
            </span>
          </td>
          <td>
            <button class="btn btn-warning btn-sm" data-toggle="modal" data-target="#myModal" (click)="viewTask(task._id)">Edit</button>
          </td>
          <td>
            <button class="btn btn-danger btn-sm" (click)="deleteTask(task._id)">Delete</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

<!-- Modal -->
<div class="modal fade" id="myModal" tabindex="-1" role="dialog">
  <div class="modal-dialog modal-lg">
    <div class="modal-content">
      <div class="modal-header bg-primary text-white">
        <h5 class="modal-title">Add / Edit Task</h5>
        <button type="button" class="close" data-dismiss="modal">&times;</button>
      </div>
      <div class="modal-body">
        <form #myForm (submit)="addeditTask(_id);reloadpage();myForm.reset();">
          <div class="form-group">
            <label>Task Name</label>
            <input type="text" [(ngModel)]="task_name" name="task_name" class="form-control" required>
          </div>
          <div class="form-check">
            <input type="checkbox" class="form-check-input" [(ngModel)]="complete" name="complete">
            <label class="form-check-label">Completed?</label>
          </div>
          <button type="submit" class="btn btn-success mt-3">Save</button>
        </form>
      </div>
    </div>
  </div>
</div>
